mapper.update:

	UPDATE TB_BOARD 
	SET
		BOARD_TITLE = #{TITLE},
		BOARD_CONTENTS = #{CONTENTS}
	WHERE
		SEQ = #{IDX}

mapper.select:
SELECT SEQ, ETP_NM, FORMAT(PAY_AM,0) AS PAY_AM, PAY_DIV, EMAIL, PAY_DIV_CD, PAY_GOODS, DATE_FORMAT(CREA_DTM, '%Y-%m-%d %h:%m') AS USE_DATE_ORG, (SELECT CAST(CREA_DTM AS DATE)) AS USE_DATE, (SELECT FORMAT(SUM(PAY_AM),0) FROM TB_USER_DATA WHERE EMAIL= #{eMail} AND CREA_DTM BETWEEN #{startDate} AND #{endDate}) AS TOTAL_AM, (SELECT COUNT(SEQ) FROM TB_USER_DATA WHERE EMAIL= #{eMail} AND CREA_DTM BETWEEN #{startDate} AND #{endDate}) AS TOTAL_CNT FROM TB_USER_DATA WHERE EMAIL = #{eMail} AND CREA_DTM BETWEEN #{startDate} AND #{endDate}
AND PAY_DIV_CD = #{divCd}
ORDER BY CREA_DTM DESC
ORDER BY convert(`PAY_AM`, decimal) DESC
ORDER BY convert(`PAY_AM`, decimal) ASC
LIMIT #{addStartCnt},10

SELECT SEQ, EMAIL, ETP_NM, FORMAT(PAY_AM,0) AS PAY_AM, PAY_DIV, PAY_DIV_CD, PAY_GOODS, DATE_FORMAT(CREA_DTM, '%Y-%m-%d %h:%m') AS USE_DATE_ORG, (SELECT CAST(CREA_DTM AS DATE)) AS USE_DATE, (SELECT FORMAT(SUM(PAY_AM),0) FROM TB_USER_DATA WHERE EMAIL = #{eMail} AND CREA_DTM BETWEEN #{startDate} AND #{endDate} AND PAY_DIV_CD = #{divCd}) AS TOTAL_AM, (SELECT COUNT(SEQ) FROM TB_USER_DATA WHERE EMAIL = #{eMail} AND CREA_DTM BETWEEN #{startDate} AND #{endDate} AND PAY_DIV_CD = #{divCd}) AS TOTAL_CNT FROM TB_USER_DATA WHERE EMAIL = #{eMail} AND CREA_DTM BETWEEN #{startDate} AND #{endDate} AND PAY_DIV_CD = #{divCd}
ORDER BY CREA_DTM DESC
ORDER BY convert(`PAY_AM`, decimal) DESC
ORDER BY convert(`PAY_AM`, decimal) ASC
LIMIT 0,10;

mapper.update:
		
			UPDATE TB_USER_DATA 
			SET
				REC_VIEW_YN 	=  'Y',
				FNL_MOF_DTM 	=	now(), 
				FNL_MDF_NM		= 'Admin'				
			WHERE
				USER_KEY = #{telNo}
			AND
				SALES_BARCODE = #{barcode}		
		
		
                AND UPLUS_USER_KEY = #{userKey}


	UPDATE TB_USER 
	SET
		PUSH_KEY 				#{pushKey}
	WHERE
		TEL_NO = #{telNo}

mapper.select:

SELECT
	PWD
FROM
	TB_USER
WHERE 
	TEL_NO = #{telNo}
AND
	USER_NM = #{userNm}
AND
	TEL_COM_CD = #{telComDiv}
AND
	EMAIL = #{email}

mapper.update:

	UPDATE TB_USER
	SET
		PUSH_YN = #{pushYn}
	WHERE
		TEL_NO = #{telNo}


	UPDATE TB_USER
	SET
		PWD = #{pwd}
