mapper.select:
SELECT SEQ, BOARD_DIV, BOARD_TITLE, BOARD_CONTENTS, HIT_CNT, DEL_GB, MAIN_GB, EVENT_START_DATE, EVENT_END_DATE, LINK_URL, DATE_FORMAT(CREA_DTM, '%Y-%m-%d %h:%m') AS USE_DATE_ORG FROM TB_BOARD WHERE BOARD_DIV = #{boardDiv} AND DEL_GB = 'N'
AND EVENT_END_DATE <= #{nowDay}
AND EVENT_END_DATE > #{nowDay}
ORDER BY CREA_DTM DESC

SELECT SEQ, AFFLIATE_NO, COUPON_CREA_ID, COUPON_CRE_DATE, COUPON_TITLE, COUPON_CONTENT, COUPON_NAME, COUPON_TYPE, COUPON_VAL, COUPON_TOTAL, COUPON_START_DATE, COUPON_END_DATE, COUPON_DEL, COUPON_CNT, DATE_FORMAT(COUPON_CRE_DATE, '%Y-%m-%d %h:%m') AS USE_DATE_ORG FROM TB_AFF_COUPON WHERE COUPON_DEL = 'N'
AND COUPON_END_DATE <= #{nowDay}
AND COUPON_END_DATE+1 > #{nowDay}
ORDER BY COUPON_CRE_DATE DESC

SELECT SALES_BARCODE, USER_KEY, SHOP_NAME, SHOP_BIZNO, SHOP_ADDR, SHOP_CEO, SHOP_TEL_NUM, SALES_DATE, SALES_PRINT_DATE, SALES_TYPE, SALES_SUM_FP_AMT, SALES_SUM_TAX_AMT, SALES_SUM_ALL_AMT, SALES_PAID_AMT, CASH_AMT, CASH_TYPE, CASH_DATE, CARD_AMT, CARD_INSTALLMENT, CARD_APP_NO, CARD_DATE, CARD_ICOM, CARD_NO, EMAIL, POINT_CARD FROM TB_USER_DATA
WHERE SHOP_BIZNO = #{biz} AND SALES_BARCODE = #{barcode}
WHERE USER_KEY = #{telNo} AND UPLUS_USER_KEY = #{userKey} AND SHOP_BIZNO = #{bizNo} AND SALES_BARCODE = #{barcode}

SELECT SALES_BARCODE , SALES_SEQ_NO , USER_KEY , SALES_PNAME , SALES_PPRICE , SALES_OPRICE , SALES_QTY , SALES_DF_AMT , SALES_FP_AMT , SALES_TAX_AMT , SALES_SL_AMT , SALES_OP_AMT , SALES_TYPE , DATE_FORMAT(FRT_CREA_DTM,'%Y-%m-%d %H:%i:%s') AS FRT_CREA_DTM FROM TB_USER_DATA_DETAIL
WHERE SHOP_BIZNO = #{biz} AND SALES_BARCODE = #{barcode}
WHERE USER_KEY = #{telNo} AND UPLUS_USER_KEY = #{userKey} AND SHOP_BIZNO = #{bizNo} AND SALES_BARCODE = #{barcode}
